settings.custom_liquid:
{%- capture contentForQuerystring -%}{{ content_for_header }}{%- endcapture -%}
{%- assign pageUrl = contentForQuerystring
| split: '"pageurl":"'
| last
| split: '"'
| first
| split: '.myshopify.com'
| last
| replace: '\/', '/'
| replace: '%20', ' '
| replace: '\u0026', '&'
-%}
{% assign query_string = '' %}
{% assign query_params = pageUrl | split: '?' | last | split: '&' %}
{% for param in query_params %}
{% assign key_value = param | split: '=' %}
{% assign key = key_value[0] | strip %}
{% assign value = key_value[1] | default: '' | strip %}
{% unless key == 'iframe_url' %}
{% assign query_string = query_string | append: '&' | append: key | append: '=' | append: value %}
{% endunless %}
{% endfor %}
<iframe style="border: none; width: 100%; height: 800px; aspect-ratio: 16/9;"
        src="https://hop-flashers-command-concert.trycloudflare.com/iframes/return-self-service?shopifystore={{ shop.permanent_domain | split: '.' | first }}&domain={{ request.host }}{{ query_string }}"
        allowfullscreen="">
</iframe>
